!function(){"use strict";class e{constructor(){this.menuBtnEl=document.querySelector(".main-header__menu-button"),this.popUpOverlayEl=document.querySelector(".pop-up__overlay"),this.menuLeftEl=document.querySelector(".main-header__menu-left"),this.menuCloseEl=document.querySelector(".main-header__menu-close")}addButtonsMenuClickListener(){this.menuBtnEl.addEventListener("click",()=>{this.popUpOverlayEl.style.display="block",this.menuLeftEl.classList.toggle("show")}),this.menuCloseEl.addEventListener("click",()=>{this.popUpOverlayEl.style.display="none",this.menuLeftEl.classList.remove("show")}),this.popUpOverlayEl.addEventListener("click",()=>{this.popUpOverlayEl.style.display="none",this.menuLeftEl.classList.remove("show")}),this.changeStyleLinkMenu()}changeStyleLinkMenu(){switch(document.location.pathname.replace(/\/kashirka.ru/,"")){case"/townships.html":this.getLinkMenu("Поселки");break;case"/plots.html":this.getLinkMenu("Участки");break;case"/all-news.html":this.getLinkMenu("Новости");break;case"/about-company.html":this.getLinkMenu("О компании");break;case"/contacts.html":this.getLinkMenu("Контакты")}}getLinkMenu(t){document.querySelectorAll(".main-header__menu-nav-link").forEach(e=>{e.textContent==t&&e.classList.add("accent")})}}window.addEventListener("load",()=>{(new e).addButtonsMenuClickListener()})}();